const int BlockSize = 512;
char* WriteBuf = new char[3*BlockSize];
// Align to block size
char * WriteStart = reinterpret_cast(((reinterpret_cast(WriteBuf) / BlockSize) + 1) * BlockSize);
WriteStart[0] = 'A';

char* ReadBuf = new char[2*BlockSize];
char * ReadStart = reinterpret_cast(((reinterpret_cast(ReadBuf) / BlockSize) + 1) * BlockSize);

EnableFaultSim();
if ( write(FDs[0], WriteStart, 2*BlockSize) == -1 )
{
	Error("Cannot write to file.", Fail);
}

Unres(lseek(FDs[0], SEEK_SET, 0) == -1, "Cannot seek to the beginning of file.");

if ( read(FDs[0], ReadStart, BlockSize) == -1 )
{
	Error("Cannot read from file.", Fail);
}

if ( ReadStart[0] != WriteStart[0] )
{
	Error("Different values were written and read.", Fail);
}

