Test.Code:
Unres(1, "Needs to be refactored");
struct stat statbuf;
mode_t setMask = 0077;
unsigned mode;
mode_t retMask;
bool firstCallSkiped = false;
EnableFaultSim();
for(umask(setMask); setMask < 01000; retMask = umask(++setMask))
{
	if(firstCallSkiped)
	{
		Fail(retMask != setMask - 1, "Returned by the function mask is wrong");					
	}

	firstCallSkiped = true;

	int fd = creat("testfile", 0777);
	Unres(fd == -1, "Unable to create file");

	if(fstat(fd, &statbuf) != 0) 
	{
		unlink("testfile");
		Unres(true, "Cannot fstat file");
	}

	mode = statbuf.st_mode & 0777;
	if (mode != (~setMask & 0777))
	{
		unlink("testfile");
		Fail(true, "Wrong mask is set");
	}
	unlink("testfile");
}
return Success;
