TestSet.Internal:
bool statsEqual(struct stat stat1, struct stat stat2)
{	
	//dev_t st_dev
	if(MAJOR(stat1.st_dev) != MAJOR(stat2.st_dev) || MINOR(stat1.st_dev) != MINOR(stat2.st_dev))
	{
		return false;
	}

	//ino_t st_ino
	if(stat1.st_ino != stat2.st_ino)
	{
		return false;
	}

	//mode_t st_mode
	if(stat1.st_mode != stat2.st_mode)
	{
		return false;
	}

	//nlink_t st_nlink
	if(stat1.st_nlink != stat2.st_nlink)
	{
		return false;
	}

	//uid_t st_uid
	if(stat1.st_uid != stat2.st_uid)
	{
		return false;
	}

	//gid_t st_gid
	if(stat1.st_gid != stat2.st_gid)
	{
		return false;
	}	

	//devt_t st_rdev
	if(MAJOR(stat1.st_rdev) != MAJOR(stat2.st_rdev) || MINOR(stat1.st_rdev) != MINOR(stat2.st_rdev))
	{
		return false;
	}

	//off_t st_size
	if(stat1.st_size != stat2.st_size)
	{
		return false;
	}	

	//blksize_t st_blksize
	if(stat1.st_blksize != stat2.st_blksize)
	{
		return false;
	}

	//blkcnt_t st_blocks
	if(stat1.st_blocks != stat2.st_blocks)
	{
		return false;
	}

	//time_t st_atime
	if(stat1.st_atime != stat2.st_atime)
	{
		return false;
	}

	//time_t st_mtime
	if(stat1.st_mtime != stat2.st_mtime)
	{
		return false;
	}

	//time_t st_ctime
	if(stat1.st_ctime != stat2.st_ctime)
	{
		return false;
	}

	return true;
}

bool cloexecFlagOff(int flag)
{
	if((flag & FD_CLOEXEC) == 1)
	{
		return false;
	}	

	return true;
}

bool statusFlagsEqual(int flag1, int flag2)
{
	if(flag1 != flag2)
	{
		return false;
	}	

	return true;
}

Test.Code:
int status = Success;
EnableFaultSim();
int newFd = dup(FDs[0]);

if (newFd == -1)
{				
	Error("System call dup failed", Fail);
}	

struct stat oldFileStat, newFileStat;
if(fstat(FDs[0], &oldFileStat) == -1 || fstat(newFd, &newFileStat) == -1)
{
	Error("System call fstat failed");
	status = Unresolved;
}
else if(!statsEqual(oldFileStat, newFileStat))
{
	Error("System call dup failed: first and second arguments fd's stats are not equal");
	status = Fail;
}

int newFlag = fcntl(FDs[0], F_GETFD);
if(newFlag < 0)
{
	Error("System call fcntl failed")
	status = Unresolved;
}	
else if(!cloexecFlagOff(newFlag))
{
	Error("System call dup failed: O_CLOEXEC flag isn't off");
	status = Fail;
}

int statusFlag = fcntl(FDs[0], F_GETFL);
int newStatusFlag = fcntl(newFd, F_GETFL);
if(statusFlag < 0 || newStatusFlag < 0)
{
	Error("System call fcntl failed");
	status = Unresolved;		
}
else if(!statusFlagsEqual(statusFlag, newStatusFlag))
{
	Error("System call dup failed: first and second arguments fd's status flags are not equal");
	status = Fail;
}

int fdOffset = lseek(FDs[0], 0, SEEK_CUR);
int newFdOffset = lseek(newFd, 0, SEEK_CUR);
if(fdOffset == -1 || newFdOffset == -1)
{
	Error("System call lseek failed");
	status = Unresolved;
} 
else if(fdOffset != newFdOffset)
{
	Error("System call dup failed: first and second arguments fd's offsets are not equal");
	status = Fail;
}

return status;

