int status = Success;
int newFd = -2;

if(dup3(-1, newFd, 0) != -1 || errno != EBADF)
{
	Error("EBADF error expected: first argument fd is not an open fd. Error code");
	status = Fail;
}

newFd = -1;

if(dup3(FDs[0], newFd, 0) != -1 || errno != EBADF)
{
	Error("EBADF error expected: second argument fd is out of the allowed range for fds");
	status = Fail;
}

return status;

int status = Success;
