const char * NewFilePath = "new_file";
const char * OrigBuf = "asdf";
char NewBuf[10];
EnableFaultSim();
Unres(link(FilePaths[0].c_str(), NewFilePath) == -1, "Cannot create link.");

Unres( write(FDs[0], OrigBuf, strlen(OrigBuf)) != static_cast(strlen(OrigBuf)), "Error in writing to original file." );

File new_file(NewFilePath, 0777, O_RDONLY);

Unres( read(new_file.GetFileDescriptor(), NewBuf, strlen(OrigBuf) ) != static_cast(strlen(OrigBuf)), "Error reading data from new file." );

if ( strncmp(OrigBuf, NewBuf, strlen(OrigBuf)) != 0 )
{
	Error("Different data was retrieved from the new file.", Fail);
}

return Success;
