Test.Code:
const string buff = "abc";
if(write(FDs[0], buff.c_str(), buff.size()) == -1)
{
	Error("System call write failed", Unresolved);
}

void * addr = mmap(0, buff.size(), PROT_READ, MAP_PRIVATE, FDs[0], 0);
if(addr == MAP_FAILED)
{
	Error("System call mmap failed", Unresolved);
}
EnableFaultSim();
if(msync(addr, buff.size(), MS_INVALIDATE | MS_SYNC) == -1)
{
	Error("System call msync failed");
	status = Fail;
}

char * readBuff = 0;
if(read(FDs[0], readBuff, buff.size()) == -1)
{
	Error("System call read failed");
	status = Unresolved;
}
else if(!strncmp(buff.c_str(), readBuff, buff.size()))
{
	Error("System call msync failed: file was not synchronized");
	status = Fail;
}

const string buff = "abc";
if(write(FDs[0], buff.c_str(), buff.size()) == -1)
{
	Error("System call write failed", Unresolved);
}

void * addr = mmap(0, buff.size(), PROT_READ, MAP_PRIVATE, FDs[0], 0);
if(addr == MAP_FAILED)
{
	Error("System call mmap failed", Unresolved);
}

if(msync(addr, buff.size(), MS_ASYNC | MS_INVALIDATE | MS_SYNC) != -1 || errno != EINVAL)
{
	Error("EINVAL error expected: flags contain invalid value");
	status = Fail;
}

if(msync(addr, buff.size(), MS_ASYNC | MS_SYNC) != -1 || errno != EINVAL)
{
	Error("EINVAL error expected: flags contain invalid value");
	status = Fail;
}

if(msync(reinterpret_cast(~PAGE_MASK), buff.size(), MS_INVALIDATE | MS_SYNC) != -1 || errno != EINVAL)
{
	Error("EINVAL error expected: address contains invalid value");
	status = Fail;			
}

int length = 3;	
void * addr = mmap(0, length, PROT_READ, MAP_PRIVATE, FDs[0], 0);
if(addr == MAP_FAILED)
{
	Error("System call mmap failed", Unresolved);	
}

if(munmap(addr, length) == -1)
{
	Error("System call munmap failed", Unresolved);
}
ErrorTest(msync(addr, length, MS_SYNC | MS_INVALIDATE), -1, ENOMEM);
