TestSet.Header:
// QFMT_VFS_V0 is defined in linux/quota.h. On 64-bit systems this file cannot be included
// because quotactl is defined in different ways in those files...
#ifndef QFMT_VFS_V0
#define QFMT_VFS_V0 2
#endif
			Unres( strcmp(DeviceName, "") == 0, "No device name is provided." );
			Unres( strcmp(MountPoint, "") == 0, "No mount point is provided." );
			int cmd_on = QCMD(Q_QUOTAON, USRQUOTA);
			int cmd_off = QCMD(Q_QUOTAOFF, USRQUOTA);
			// Create the quota file
			UnixCommand* quotacheck = new UnixCommand(QUOTACHECK_PATH);
			vector quota_args;
			quota_args.push_back(MountPoint);

			ProcessResult * res = quotacheck->Execute(quota_args);
			delete quotacheck;
			Unres( res == NULL, "Cannot execute 'quotacheck' command to create the quota file." );
			Unres( res->GetStatus() != Success, "Error executing 'quotacheck'. " + res->GetOutput() );
			string QuotaFile = MountPoint;
			QuotaFile += "/aquota.user";

Test.Code:

Fail( quotactl(cmd_on, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1, "Cannot enable quotas V0." );

Fail( quotactl(cmd_off, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1, "Cannot disable quotas V0." );

return Success;

int cmd_get_quota = QCMD(Q_GETQUOTA, USRQUOTA);
int cmd_set_quota = QCMD(Q_SETQUOTA, USRQUOTA);

Unres( quotactl(cmd_on, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1, "Cannot enable quotas V0." );

struct dqblk quota_data, quota_data2;
memset(&quota_data, 0, sizeof(quota_data));
memset(&quota_data2, 0, sizeof(quota_data2));

quota_data.dqb_bhardlimit = 1000;
quota_data.dqb_valid = QIF_BLIMITS;

Fail( quotactl(cmd_set_quota, DeviceName, 0, (caddr_t)&quota_data ) == -1, "Cannot set quota." );

Fail( quotactl(cmd_get_quota, DeviceName, 0, (caddr_t)&quota_data2 ) == -1, "Cannot get quota." );

Fail( quota_data.dqb_bhardlimit != quota_data2.dqb_bhardlimit, "Different values after set-get operation." );

if ( quotactl(cmd_off, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1)
	cerr << "Cannot disable quotas V0.";

return Success;


int cmd_get_info = QCMD(Q_GETINFO, USRQUOTA);
int cmd_set_info = QCMD(Q_SETINFO, USRQUOTA);

Unres( quotactl(cmd_on, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1, "Cannot enable quotas V0." );

struct dqinfo quota_info, quota_info2;
memset(&quota_info, 0, sizeof(quota_info));
memset(&quota_info2, 0, sizeof(quota_info2));

quota_info.dqi_bgrace = 100;
quota_info.dqi_valid = IIF_BGRACE;

Fail( quotactl(cmd_set_info, DeviceName, 0, (caddr_t)&quota_info ) == -1, "Cannot set info." );

Fail( quotactl(cmd_get_info, DeviceName, 0, (caddr_t)&quota_info2 ) == -1, "Cannot get info." );

Fail( quota_info.dqi_bgrace != quota_info2.dqi_bgrace, "Different values after set-get operation." );

if ( quotactl(cmd_off, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1)
	cerr << "Cannot disable quotas V0.";

return Success;

int cmd_get_format = QCMD(Q_GETFMT, USRQUOTA);
int format;

Unres( quotactl(cmd_on, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1, "Cannot enable quotas V0." );

Fail( quotactl(cmd_get_format, DeviceName, 0, (caddr_t)&format ) == -1, "Cannot get format." );

Fail( format != QFMT_VFS_V0, "Format value is not correct" );

if ( quotactl(cmd_off, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1)
	cerr << "Cannot disable quotas V0.";

return Success;

int cmd_sync = QCMD(Q_SYNC, USRQUOTA);

Unres( quotactl(cmd_on, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1, "Cannot enable quotas V0." );

Fail( quotactl(cmd_sync, DeviceName, 0, 0 ) == -1, "Cannot sync." );

if ( quotactl(cmd_off, DeviceName, QFMT_VFS_V0, (char*)QuotaFile.c_str()) == -1)
	cerr << "Cannot disable quotas V0.";

return Shallow;
