const char * buf = (char*)"1234";
char buf2[10];
loff_t src_len = 0;
int pipe_fds[2];

Unres( pipe(pipe_fds) == -1, "Cannot create pipe." );

Unres( write(FDs[0], buf, strlen(buf)) != (ssize_t)strlen(buf), "Error writing to file.");

EnableFaultSim();
Fail ( splice(FDs[0], &src_len, pipe_fds[1], NULL, strlen(buf), 0) == -1, "Cannot splice.");

Unres( read(pipe_fds[0], (void*)buf2, strlen(buf)) != (ssize_t)strlen(buf), "Cannot read from pipe." );
buf2[strlen(buf)] = 0;

Fail ( strncmp(buf, buf2, strlen(buf)) != 0, "Data corruption" );

return Success;

