Test.Code:
JFSCtl jfs("/dev/ram0");

logsuper sb;

jfs.ReadBlock(LOGSUPER_B, &sb, sizeof(sb));

sb.magic = 1;

jfs.WriteBlock(LOGSUPER_B, &sb, sizeof(sb));

Check( mount( DeviceName, MountPoint, FileSystem, 0, 0) == 0, "Mount should fail.");

JFSCtl jfs("/dev/ram0");

logsuper sb;

jfs.ReadBlock(LOGSUPER_B, &sb, sizeof(sb));

sb.state = LOGREDONE + 1;

jfs.WriteBlock(LOGSUPER_B, &sb, sizeof(sb));

Check( mount( DeviceName, MountPoint, FileSystem, 0, 0) == 0, "Mount should fail.");

JFSCtl jfs("/dev/ram0");

logsuper sb;

jfs.ReadBlock(LOGSUPER_B, &sb, sizeof(sb));

memcpy(sb.uuid, "abracadabra12345", 16);

jfs.WriteBlock(LOGSUPER_B, &sb, sizeof(sb));

Check( mount( DeviceName, MountPoint, FileSystem, 0, 0) == 0, "Mount should fail.");
