						Unres(! strcmp(DeviceName, ""), "No partition name is provided.");
                        Unres(! strcmp(FileSystem, ""), "No file system type is provided.");
                        Unres(! strcmp(MountPoint, ""), "No mount point is provided.");
                        int res;
                        Unres ( chdir("/") == -1, "Cannot change directory." );
