						{
							if( res == PS_Fatal )
							{
								Error("Device too small.");
								Return(Unresolved);
							}						
							Error("Partition mount or umount failed");
							Return(Unresolved);	
						}

                        res = mount( DeviceName, MountPoint, FileSystem, MS_REMOUNT, "resize") ;


